import { API } from 'Infrastructure/APIS';
import MaterialTable from 'material-table';
import React, { useEffect, useState } from 'react';


// import { Container } from './styles';

function Screens() {
    const [frequencia, setFrequencia]: any = useState(); // a que ser치 renderizada na tabela.
    const [NovaFrequencia, setNovaFrequencia]: any = useState() // a quqe ser치 enviada para atualizar no backend


    const columns: any = [
        { title: "Nome", field: "nome", editable: false },
        { title: "Cargo", field: "cargo" },
        { title: "janeiro", field: "janeiro" },
        { title: "abril", field: 'abril', },
        { title: "maio", field: "maio", },
        { title: "junho", field: "junho", },
        { title: "julho", field: "julho", },
        { title: "agosto", field: "agosto", },
        { title: "setembro", field: "setembro", },
        { title: "outubro", field: "outubro", },
        { title: "novembro", field: "novembro", },
        { title: "dezembro", field: "dezembro", },
      ]

    useEffect(() =>{
        (async () => {
            const { data } = await API.get("/servidor/frequencia");
            const frequency = data.map((a:any, key: any) =>{
              let obj1 = a.frequencia
              let obj2 = Object.assign(a,obj1[key])
              return obj2
            })
            setFrequencia(frequency);
          })();
    },[])

    return (
        <div>
            <MaterialTable
                data={frequencia}
                columns={columns}
                editable={{
                    onRowAdd: (newRow) => new Promise((resolve:any, reject) => {
                      const updatedRows = [...frequencia, { id: Math.floor(Math.random() * 100), ...newRow }]
                      setTimeout(() => {
                        setFrequencia(updatedRows)
                        setNovaFrequencia(newRow)
                        resolve()
                      }, 2000)
                    }),
                    onRowDelete: (selectedRow:any) => new Promise((resolve:any, reject) => {
                      const index = selectedRow.tableData.id;
                      const updatedRows = [...frequencia]
                      updatedRows.splice(index, 1)
                      setTimeout(() => {
                        setFrequencia(updatedRows)
                        resolve()
                      }, 2000)
                    }),
                    onRowUpdate:(updatedRow,oldRow)=>new Promise((resolve:any,reject)=>{
                      const index= oldRow.tableData.id;
                      const updatedRows=[...frequencia]
                      updatedRows[index]=updatedRow
                      setTimeout(() => {
                        setFrequencia(updatedRows)
                        resolve()
                    }, 2000)
                    })

                  }}
                  options={{
                    actionsColumnIndex: -1, addRowPosition: "first"
                  }}
            />
        </div>
    )
}

export default Screens;
